/// Regular font family
/// @type List
$main-text-stack: "Lato", "Helvetica Neue Light", "Helvetica Neue", "Helvetica",
  "Arial", sans-serif !default;

/// Code (monospace) font family
/// @type List
$code-font-stack: "Cabin", "DejaVu Sans Mono", "Bitstream Vera Sans Mono",
  "Monaco", monospace !default;


//COLORS!;
$primary-color:rgba(255,204,74);
$primary-color-02:"#4EAF4E";
